// Check if domain already exists
const domains = $input.first().json.data || [];
const config = $('Configuration').first().json;
const targetDomain = config.domain;

const existingDomain = domains.find(d => d.name === targetDomain);

if (existingDomain) {
  return [{
    json: {
      ...config,
      domain_exists: true,
      domain_id: existingDomain.id,
      domain_status: existingDomain.status,
      domain_records: existingDomain.records || []
    }
  }];
}

return [{
  json: {
    ...config,
    domain_exists: false,
    domain_id: null,
    domain_status: 'not_created'
  }
}];

// Prepare DNS records for output
const domainData = $input.first().json;
const config = $('Configuration').first().json;

// Handle both existing domain and newly created domain
let records = domainData.records || domainData.domain_records || [];
let domainId = domainData.id || domainData.domain_id;
let status = domainData.status || domainData.domain_status;

return [{
  json: {
    domain: config.domain,
    domain_id: domainId,
    status: status,
    dns_records: records,
    setup_instructions: records.length > 0 ? 
      'Add the following DNS records to your domain provider' : 
      'DNS records will be available once the domain is processed'
  }
}];

// Check if webhook already exists for our endpoint
const webhooks = $input.first().json.data || [];
const config = $('Configuration').first().json;
const domainInfo = $('Format Domain Info').first().json;
const targetEndpoint = config.webhook_endpoint;

const existingWebhook = webhooks.find(w => w.endpoint_url === targetEndpoint);

return [{
  json: {
    ...domainInfo,
    webhook_endpoint: targetEndpoint,
    events_to_subscribe: config.events_to_subscribe,
    webhook_exists: !!existingWebhook,
    existing_webhook_id: existingWebhook?.id || null
  }
}];

// Generate final setup report
const input = $input.first().json;
const config = $('Configuration').first().json;
const domainInfo = $('Format Domain Info').first().json;

// Check if this is from the webhook creation or the existing webhook path
const webhookId = input.id || input.existing_webhook_id;
const webhookSecret = input.signing_secret || 'Use existing webhook secret';

const report = {
  setup_complete: true,
  timestamp: new Date().toISOString(),
  domain: {
    name: config.domain,
    id: domainInfo.domain_id,
    status: domainInfo.status,
    dns_records: domainInfo.dns_records
  },
  webhook: {
    id: webhookId,
    endpoint: config.webhook_endpoint,
    events: config.events_to_subscribe,
    signing_secret: webhookSecret
  },
  next_steps: [
    'Add DNS records to your domain provider',
    'Wait for domain verification (usually 24-48 hours)',
    'Store the webhook signing secret securely',
    'Configure your Resend Trigger node with the signing secret'
  ]
};

return [{ json: report }];
